import {Component} from '@angular/core';

@Component({
  selector: 'nz-demo-drawer-header-job',
  template: `
    <sc-drawer-header
      scRecordType="job"
      scRecordIconType="sc:briefcase"
      scAvatarThumbnailSrc="https://pbs.twimg.com/profile_images/1337607516008501250/6Ggc4S5n_400x400.png"
      scAvatarSrc="https://pbs.twimg.com/profile_images/1337607516008501250/6Ggc4S5n_400x400.png"
      scAvatarText="Tesla"
      scOptionalText="ID: 58050747 | Tesla"
      [scSwitchEntityEnabled]="true"
    >
      <ng-template #headerTitle>
        Tesla!
      </ng-template>
      <ng-template #infoLine>
        <sc-status-select [selectedStatus]="status" [statuses]="progressStatus" [showStage]="true"
                          [showClosed]="true"></sc-status-select>
        <nz-tag class="tag">INTERVIEW</nz-tag>
        <sc-button scButtonSize="small" scButtonType="text" class="margin">Invoice Schedule</sc-button>
        <strong>$60000-$100000 per year</strong>
      </ng-template>
      <ng-template #quickActions>
        <div class="quick-actions">
          <button nz-button nzType="text" nzSize="small"><i nz-icon nzType="mail" [nzTheme]="'fill'"></i></button>
          <nz-divider class="divider" nzType="vertical"></nz-divider>
          <button nz-button nzType="text" nzSize="small"><i nz-icon nzType="phone" [nzTheme]="'fill'"></i></button>
          <nz-divider class="divider" nzType="vertical"></nz-divider>
          <button nz-button nzType="text" nzSize="small"><i nz-icon nzType="message" [nzTheme]="'fill'"></i></button>
          <nz-divider class="divider" nzType="vertical"></nz-divider>
          <button nz-button nzType="text" nzSize="small"><i nz-icon nzType="pushpin" [nzTheme]="'fill'"></i></button>
        </div>
      </ng-template>
      <ng-template #actionMenu>
        <sc-button nzTrigger="click" nz-dropdown [nzDropdownMenu]="menu" nz-button scButtonType="default" scButtonSize="small"
                   [(nzVisible)]="actionMenuOpen" scIconDirection="right" [scIconType]="actionMenuOpen ? 'up' : 'down'">
          Actions
        </sc-button>
        <nz-dropdown-menu #menu="nzDropdownMenu">
          <ul nz-menu nzSelectable>
            <li nz-menu-item>1st menu item</li>
            <li nz-menu-item>2nd menu item</li>
            <li nz-menu-item>3rd menu item</li>
            <li nz-menu-item nzDanger>4th danger item</li>
          </ul>
        </nz-dropdown-menu>
      </ng-template>
      <ng-template #tabMenu>
        <!--      special link tab-set where links are connected to the route-->
        <nz-tabset
          id="record-navigation-tab"
          [nzAnimated]="true"
          nzLinkRouter
          (nzSelectedIndexChange)="tabChanged($event)"
        >
          <nz-tab *ngFor="let tab of menuItems">
            <a
              *nzTabLink
              nz-tab-link
              [routerLink]="['.']"
              [queryParams]="{ activeTab: tab.index }"
              queryParamsHandling="merge"
            >
              {{ tab.name }}
              <span *ngIf="tab.subTitle && tab.subTitle > 0">({{ tab.subTitle }})</span>
              <i *ngIf="tab.hasUnread" class="fa fa-circle">.</i>
            </a>
          </nz-tab>
        </nz-tabset>
      </ng-template>
      <ng-template #windowActions>
        <button nz-button nzType="text"><i nz-icon nzType="sc:side-panel"></i></button>
      </ng-template>
    </sc-drawer-header>
  `,
  styles: [
    `
      .margin {
        margin: 0 8px 0 0;
      }

      .quick-actions {
        display: inline-block;

        button[nzType='text'] {
          color: #8893a7;
          margin-right: 8px;
        }
      }

      .tag {
        margin-left: 8px;
        border: none;
      }

      i.fa.fa-circle {
          font-size: 25px;
          position: absolute;
          padding-left: 10px;
          color: #1971c2;
          top: -15px;
        }

      i.app-icon {
        font-size: 10px;
        vertical-align: baseline;
      }
    `
  ]
})
