export interface Status {
  statusID: number;
  name: string;
  closed: boolean;
  default: boolean;
  rejected: boolean;
  stage: Stage | null;
}

export interface Stage {
  stageID: number;
  name: string;
  progress: string; // possible value: inactive, started, inprogress, complete
  displayIndex: number;
  default: boolean;
  forecast: number;
  workflowCount?: number;
  usedInWorkflows?: Workflow[];
  isDeleting?: boolean;
}

export interface Workflow {
  accountID: number;
  workflowID: number;
  name: string;
  placementType: string;
  isDefault: boolean;
  stages: WorkflowStage[];
  userGroups: number[];
  userGroupNames: string[];
}

export interface WorkflowStage {
  stageID: number;
  displayIndex: number;
  progress: string; // possible value: inactive, started, inprogress, complete
  forecast: number;
  stage: Stage;
}
