}
const CandidateActivityTypeDescription: { [key: string]: string } = {
  Screening: 'Screening',
  ReferenceCheck: 'Reference Check',
  CandidateCare: 'Candidate Care',
  BackgroundCheck: 'Background Check',
  VisaWorkRights: 'Visa / Work Rights',
  Onboarding: 'Onboarding',
  Compliance: 'Compliance',
  Induction: 'Induction',
