};
interface Company {
  companyID: number;
  name: string;
  initials: string;
  imageUrl: string;
  imageThumbnailUrl: string;
  primaryContact: Contact;
  addresses: Address[];
  remoteLookedUp: boolean;
  address: Address;
  otherAddresses: Address[];
  parentCompany: Company;
  socialLinks: SocialLinks;
  customFields: Map<number, CustomFieldValue>;
