}
interface WorkflowStage {
  stageID: number;
  displayIndex: number;
  progress: string; // possible value: inactive, started, inprogress, complete
  forecast: number;
  stage: Stage;
}

interface Workflow {
  accountID: number;
  workflowID: number;
  name: string;
  placementType: string;
  isDefault: boolean;
  stages: WorkflowStage[];
  userGroups: number[];
  userGroupNames: string[];
}
interface Stage {
  stageID: number;
  name: string;
  progress: string; // possible value: inactive, started, inprogress, complete
  displayIndex: number;
  default: boolean;
  forecast: number;
  workflowCount?: number;
