}
interface Status {
  statusID: number;
  name: string;
  closed: boolean;
  default: boolean;
  rejected: boolean;
  stage: Stage;
}

interface JobApplication {
  jobApplicationID: number;
  jobOrderID: number;
  jobOrderWorkflowID: number;
  jobOrderSource: string;
