  jobTitle: string;
  company?: Company;
  candidate: Candidate;
  status: Status;
  hasPlacement: boolean;
  source: string;
  searchRank: number;
  location: string;
  locationDisplayName: string;
  phone: PhoneNumber;
  mobile: PhoneNumber;
  inboundJobApplicationCount?: number;
  hasCoverLetterVideo: boolean;
  address: Address;
  idealPosition: string;
  idealSalary: WorkTypeSalary;
  owner: UserLink;
  recruiter: UserLink;
