  noteSummary: NoteSummary;
  salutation: string;
  linkedInUrl: string;
  jobApplicationPopupUrl: string;
  reviewStage: string;
  rating: number;
  candidateInternalRating: string;
  dateCreated: string;
  dateUpdated: string;
  dataPolicyConsentAtUtc: string;
  partnerAction: PartnerActionStatus[];
