  In1Week = 'In1Week',
  In2Weeks = 'In2Weeks',
  In3Weeks = 'In3Weeks',
  In4Weeks = 'In4Weeks',
  In1Month = 'In1Month',
  In2Months = 'In2Months',
  In3Months = 'In3Months',
  In4Months = 'In4Months',
  In5Months = 'In5Months',
  In6Months = 'In6Months',
}
const AccountFeatures = {
  email: 'email',
  sms: 'sms',
  jobOrders: 'jobOrders',
  folders: 'folders',
  jobApplications: 'jobApplications',
  candidates: 'candidates',
  candidateAttachments: 'candidateAttachments',
  candidateSkills: 'candidateSkills',
  candidateSubmissions: 'candidateSubmissions',
  candidateSubmissionComments: 'candidateSubmissionComments',
  placements: 'placements',
  companies: 'companies',
  contacts: 'contacts',
  skills: 'skills',
  careerUpdate: 'careerUpdate',
  seekTSC: 'seekTSC',
  subCategory: 'subCategory',
  ratesAdder: 'ratesAdder',
};
enum FieldType {
  Text,
  List,
  Date,
  Lookup,
  Url,
}
class PartnerMenuActionItem {
  actionID: string;
  name: string;
  activeOnly: boolean;
  approvedOnly: boolean;
  url: string;
  urlType: string;
  status: number[];
  enabled: boolean;
}
class PartnerMenuAction {
  partnerID: number;
  menuName: string;
  columnName: string;
  actions: PartnerMenuActionItem[];
}
interface CustomFieldName {
  fieldID: number;
  name: string;
  fieldType: FieldType;
  isMultiValue: boolean;
  isDate: boolean;
  isUrl: boolean;
  isList: boolean;
  possibleValues: string[];
  mandatory: boolean;
  maximumLength: number;
}
const StartDateTypeDescription: { [key: string]: string } = {
  NotSpecified: '',
  DateSpecified: 'Select a Date',
  Immediate: 'Immediate',
  In1Week: '1 Week',
  In2Weeks: '2 Weeks',
  In3Weeks: '3 Weeks',
  In4Weeks: '4 Weeks',
  In1Month: '1 Month',
  In2Months: '2 Months',
  In3Months: '3 Months',
  In4Months: '4 Months',
  In5Months: '5 Months',
  In6Months: '6 Months',
};
interface Candidate {
  candidateID: number;
  name: string;
  firstName: string;
  lastName: string;
  salutation: string;
  phone: PhoneNumber;
  mobile: PhoneNumber;
  email: string;
  otherEmail: string[];
  address: Address;

  currentPosition: string;
  currentEmployer: string;
  currentSalary: WorkTypeSalary;
  availability: StartDate;

  idealPosition: string;
