  status: Status;
  remoteLookedUp: boolean;

  dateOfBirth: Date;
  emergencyContactName: string;
  emergencyContactNumber: string;
  dateResumeUpdated: string;

  linkedContactID: number;

  lastPlaced: string;
  seeking: CandidateSeeking;

  isPayrollLookups: boolean;
  payrollOffice: string;
  payrollSkillGroup: string;
  payrollPosition: string;
}
interface CandidateSeeking {
  status: CandidateSeekingStatus;
  dateUpdatedUtc: string;
}
enum CandidateSeekingStatus {
  Yes = 'Yes',
  Maybe = 'Maybe',
  No = 'No',
}

const CandidateSeekingStatusDescription: { [key: string]: string } = {
  Yes: 'Actively Looking',
  Maybe: 'Open to Opportunities',
  No: 'Not Interested',
};
interface UserLink extends PersonName {
  userID: number;
  displayName: string;
  mentionName: string;
  email: string;
  initials: string;
  headshotUrl: string;
  deleted: boolean;
  fullName: string;
}
interface PersonName {
  firstName: string;
  lastName: string;
  fullName: string;
}
interface PartnerActionStatus {
  partnerID: number;
  stage: string;
  stageText: string;
  status: string;
  updated: string;
  result: PartnerActionResult;
}
interface PartnerActionResult {
  url: string;
  urlPopup: boolean;
}
interface NoteSummary {
  noteType: string;
  dateCreatedUtc: Date;
}
interface CustomFieldValue {
  fieldID: number;
  value?: string;
  valueList?: string[];
}

@Component({
  selector: 'nz-demo-grid-candidate',
  template: `
    <div
      class="manage-column"
      [(nzPopoverVisible)]="candidateActivityColumnSettingVisible"
      nz-popover
      nzPopoverTrigger="click"
      nzPopoverPlacement="bottomRight"
      [nzPopoverContent]="manageCandidateActivityColumns"
    >
      <i class="app-icon icon-primary" nz-icon nzType="ja:setting"></i>
    </div>

    <ng-template #manageCandidateActivityColumns>
      <ng-container *ngIf="activityAllColumn$ | async as activityAllColumn">
        <sc-grid-manage-column
          [scAllColumns]="activityAllColumn"
          (scClose)="candidateActivityColumnSettingVisible = false"
          (scReset)="resetGridSettings()"
          (scSave)="saveGridSettings($event)"
        ></sc-grid-manage-column>
      </ng-container>
    </ng-template>

    <sc-grid
      [scGridData]="activities"
      [scGridSetting]="aGridSettings"
      [scGridName]="gridName"
      scIdPropertyName="activityID"
      [scCanUserSelectColumn]="fullView"
      [scIsFitToParent]="!fullView"
      [scShowInlineCheckAll]="false"
      [scPageSize]="pageSize"
      [scPageIndex]="pageIndex"
      [scTotalCount]="totalCount"
      [scShowCheckbox]="false"
      [scIsServerSideSearch]="true"
      [scActiveID]="(isActivityDrawerOpen$ | async) ? activeRowID : ''"
      [scSearchKeyword]="searchKeyword"
      (scRawColumnsUpdated)="onActivitiesRawColumns($event)"
      (scFilterChange)="columnSearchChange($event)"
      (scSortChange)="onSortChange($event)"
      (scPageChange)="onPageChange($event)"
      (scPageSizeChange)="onPageSizeChange($event)"
    >
      <sc-grid-column scHeader="Record" [scFixedWidth]="70" scLeft="0" [scCanSort]="false" scTranslate="no">
        <ng-template let-item="item">
          <!-- <i class="app-icon record-type" nz-icon nzType="user"></i> -->
          <i nz-icon nzType="user" nzTheme="outline"></i>
        </ng-template>
      </sc-grid-column>
      <sc-grid-column
        scHeader="Activity Type"
        scBinding="type"
        scSortKey="type"
        [scDefaultSort]="ScSortDirection.Descend"
        [scScaleWidth]="2"
        [scCanSearch]="true"
        [scCanSearchColumn]="true"
        scTranslate="no"
      >
        <ng-template let-item="item">
          <!-- {{ getActivityTypeDisplay(item) }} -->
          <span [innerHTML]="getActivityTypeDisplay(item) | prefixHighlight: searchKeyword"> </span>
        </ng-template>
      </sc-grid-column>
      <sc-grid-column
        scHeader="Activity Name"
        scBinding="text"
        scSortKey="action name"
        [scDefaultSort]="ScSortDirection.Descend"
        [scScaleWidth]="2"
        [scCanSearch]="true"
        [scCanSearchColumn]="true"
      ></sc-grid-column>
      <sc-grid-column
        scHeader="Details"
        [scScaleWidth]="4"
        [scCanSearch]="true"
        [scDisplay]="fullView"
        [scCanSort]="false"
      >
        <ng-template let-item="item">
          <ng-container *ngIf="item.statusName">Change status to - {{ item.statusName }}.&nbsp;</ng-container>
          <ng-container *ngIf="item.details">
            <span
              *ngFor="let detail of item.details"
              class="acitivty-details"
              [innerHTML]="getDetails(detail) | prefixHighlight: searchKeyword"
            >
            </span>
            <!-- <span *ngFor="let detail of item.details" class="acitivty-details" >
              &nbsp;{{ detail.fieldName }}: {{ detail.textValue }}.
            </span> -->
          </ng-container>
        </ng-template>
      </sc-grid-column>
      <sc-grid-column
        scHeader="Job Title"
        scBinding="jobTitle"
        scSortKey="jobOrderTitle"
        [scDefaultSort]="ScSortDirection.Descend"
        [scScaleWidth]="4"
        [scCanSearch]="true"
        [scDisplay]="fullView"
        [scCanSearchColumn]="true"
        scTranslate="no"
      >
        <ng-template let-item="item">
          <a [href]="'joborders/' + item.jobOrderID">{{ item.jobTitle }}</a>
        </ng-template>
      </sc-grid-column>
      <sc-grid-column
        scHeader="Created By"
        scBinding="ownerDisplayName"
        scSortKey="createdBy"
        [scDefaultSort]="ScSortDirection.Descend"
        [scFixedWidth]="110"
        [scCanSearch]="true"
        [scCanSearchColumn]="true"
      ></sc-grid-column>
      <sc-grid-column
        scHeader="Updated"
        scBinding="updatedAtUtc"
        scSortKey="updatedDate"
        [scDefaultSort]="ScSortDirection.Descend"
        [scFixedWidth]="110"
        scDataType="dateTime"
      ></sc-grid-column>
      <sc-grid-column scHeader="Actions" scRight="0" [scFixedWidth]="70" [scCanSort]="false">
        <ng-template let-item="item">
          <span class="action-icons">
            <a [href]="getDownloadUrl(item)">
              <i
                class="download app-icon"
                nz-icon
                nzType="outline:download"
                nzTooltipTitle="Download"
                nzTooltipPlacement="top"
                nz-tooltip
              >
              </i>
            </a>

            <a
              class="view-activity identity-column clickable"
              nz-tooltip
              nzTooltipTitle="View Activity"
              nzTooltipPlacement="left"
              (click)="showActivity(item)"
            >
              <i nz-icon nzType="right"></i>
            </a>
          </span>
        </ng-template>
      </sc-grid-column>
    </sc-grid>`,
  styles: [`
    .manage-column {
      background-color: white;
      display: flex;
      justify-content: center;
      align-items: center;
      border: 1px solid #ccd0d9;
      cursor: pointer;
      border-radius: 3px;
      height: 32px;
      width: 40px;
      margin-left: auto;
    }
  `]
})
export class NzDemoGridCandidateComponent implements OnInit {
  @Input() fullView: boolean;
  @Input() searchKeyword: string = 'highlight';
  @Input() activeRowID: number;

  @Output() activeRowChange = new EventEmitter<number>();

  activities: any[];
  activityFilter: EntityActivityNoteFilter;
  isActivityDrawerOpen$: Observable<boolean>;

  columns: ScGridColumnComponent[] = [];
  columnFilters: ColumnFilterValue[];
  pageIndex = 1;
  pageSize: number;
  sortKey: string;
  sortDirection: string;
  defaultSortKey = 'updatedDate';
  defaultSortDirection = ScSortDirection.Descend;
  totalCount: number;

  gridName = 'candidateActivityGrid';

  ScSortDirection = ScSortDirection;
  cGridSettings: ScGridSetting;
  selectedIDs$: Observable<number[]>;
  cActiveID: string | number = '';
  cGrayTextIDs: (string | number)[] = [];
  aGridSettings: ScGridSetting;
  candidateActivityColumnSettingVisible: boolean;

  activityAllColumn$: Observable<ScGridColumnComponent[]>;

  constructor(
    private cdRef: ChangeDetectorRef,
    private gridService: ScGridService
  ) {}

  ngOnInit(): void {
    this.fullView = true;
    this.selectedIDs$ = of([9537426, 9537425]);
    this.cActiveID = 9537427;
    this.cGrayTextIDs = [9537615, 9537610];

    this.initAGridSettings();
    this.initActivitiesArray();
  }

  onSortChange(sort: any) {
    this.sortKey = !!sort.key ? sort.key : this.defaultSortKey;
    this.sortDirection = !!sort.value ? sort.value : this.defaultSortDirection;
    this.loadActivities();
  }

  onPageSizeChange(pageSize: number) {
    this.pageIndex = 1;
    this.pageSize = pageSize;
    this.loadActivities();
  }

  onPageChange(pageIndex: number) {
    this.pageIndex = pageIndex;
    this.loadActivities();
  }

  private primarySearch() {
    // this.store.dispatch(fromRoot.searchActivity({ searchKeyword: this.searchKeyword }));
    this.pageIndex = 1;
    this.loadActivities();
  }

  columnSearchChange(e: { columnName: string; searchText: string }[]) {
    this.columnFilters = e.map((f) => ({ columnName: f.columnName, value: f.searchText }));
    this.pageIndex = 1;
    this.loadActivities();
  }

  getActivityTypeDisplay(item: Activity) {
    if (!item.activityEntityType) {
      return '';
    }
    return `${CandidateActivityTypeDescription[item.type]} highlight`;
  }

  loadActivities() {
    this.activityFilter = {
      entityID: 1000,
      keywords: this.searchKeyword ? this.searchKeyword : '',
      page: this.pageIndex,
      pageSize: this.pageSize,
      sortKey: this.sortKey,
      sortDirection: this.sortDirection,
      columnFilterFields: this.columnFilters,
    };

    /*     this.candidateService.getActivities(this.activityFilter).subscribe((a) => {
      this.activities = a.records;
      this.totalCount = a.totalCount;
      this.store.dispatch(fromRoot.loadActivityCollectionSuccess({ activities: a.records }));
      this.cdRef.markForCheck();
    }); */
  }

  showActivity(activity: Activity) {
    /*     this.store.dispatch(
      fromRoot.OpenDrawer({
        componentToken: Inject_ActivityDetailDrawer,
        inputParams: {
          entityID: activity.activityEntityID,
          activityID: activity.activityID,
          entityType: activity.activityEntityType,
        },
        canPush: true,
        maskClosable: true,
      })
    );
 */
    this.activeRowID = activity.activityID;
    this.activeRowChange.emit(activity.activityID);
    this.cdRef.markForCheck();
  }

  onActivitiesRawColumns(data: any) {
    this.activityAllColumn$ = data;
  }
  gridSettingColumnsUpdated(data: ScGridSetting) {
    this.aGridSettings = data;
  }
  getDownloadUrl(activity: Activity) {
    return `local/${activity.activityID}/download`;
  }

  public getDetails(detail: { fieldName: string; textValue: string }): string {
    return ` ${detail.fieldName}: ${detail.textValue}.`;
  }

  updateGridSettings(gridSettings: any) {
    console.log(gridSettings);
  }

  updateGridColumnSettings(gridColumnSettings: any) {
    console.log(gridColumnSettings);
  }

  resetGridSettings(): void {
    this.initAGridSettings();
    this.candidateActivityColumnSettingVisible = false;
  }

  saveGridSettings($event: any): void {
    this.aGridSettings = {
      gridName: this.aGridSettings.gridName,
      columns: $event.columns
    } as ScGridSetting;

    this.candidateActivityColumnSettingVisible = false;
  }

  initAGridSettings(): void {
    this.aGridSettings = {
      gridName: this.gridName,
      columns: [
        { name: 'Record', width: 0.0, display: true },
        { name: 'Activity Type', width: 0.0, display: true },
        { name: 'Activity Name', width: 0.0, display: true },
        { name: 'Details', width: 366.0, display: true },
        { name: 'Job Title', width: 112.0, display: true },
        { name: 'Created By', width: 0.0, display: true },
        { name: 'Updated', width: 0.0, display: true },
        { name: 'Actions', width: 0.0, display: true },
      ],
      pageSize: 20,
      sortKey: '',
    };

    this.activityAllColumn$ = this.gridService.getAllGridColumns$(this.aGridSettings.gridName).pipe(take(1));
  }

  initActivitiesArray(): void {
    this.activities = [
      {
        activityID: 15526,
        activityEntityID: 13825556,
        activityEntityType: 'Candidate',
        jobOrderID: null,
        jobTitle: null,
        statusName: null,
        details: [{ fieldName: 'What why when how? highlight', textValue: 'Yes highlight' }],
        summary: null,
        text: 'TestTemplate highlight Action-DONOTDELETE',
        type: 'ReferenceCheck',
        ownerDisplayName: 'Allison Qu highlight',
        dateCreatedUtc: '0001-01-01T00:00:00',
        updatedAtUtc: '2021-08-24T10:09:30Z',
        updatedBy: null,
        relatedEntities: [],
        attachments: [],
      },
      {
        activityID: 15525,
        activityEntityID: 13825556,
        activityEntityType: 'Candidate',
        jobOrderID: null,
        jobTitle: null,
        statusName: null,
        details: [
          { fieldName: 'What why when how? highlight', textValue: '2021-08 highlight' },
          { fieldName: 'Comments highlight', textValue: 'test highlight' },
        ],
        summary: null,
        text: 'Automation highlight candidate activity',
        type: 'Screening',
        ownerDisplayName: 'Allison Qu highlight',
        dateCreatedUtc: '0001-01-01T00:00:00',
        updatedAtUtc: '2021-08-24T02:25:50Z',
        updatedBy: null,
        relatedEntities: [],
        attachments: [],
      },
    ];
  }

  FieldType: typeof FieldType = FieldType;
